mapper.insert:
insert into roadinfolist (id, roadid, roadcontext, 
  roadphotopath, filepath, serverurl, 
  mobiletype, recordtime, updatadate, 
  person, back, roadphoto
  )
values (#{id,jdbcType=INTEGER}, #{roadid,jdbcType=INTEGER}, #{roadcontext,jdbcType=VARCHAR}, 
  #{roadphotopath,jdbcType=VARCHAR}, #{filepath,jdbcType=VARCHAR}, #{serverurl,jdbcType=VARCHAR}, 
  #{mobiletype,jdbcType=INTEGER}, #{recordtime,jdbcType=VARCHAR}, #{updatadate,jdbcType=TIMESTAMP}, 
  #{person,jdbcType=VARCHAR}, #{back,jdbcType=VARCHAR}, #{roadphoto,jdbcType=LONGVARBINARY}
  )
